extern crate reqwest;

use std::io::Read;

fn construct_headers() -> HeaderMap {
    let mut headers = HeaderMap::new();
    headers.insert("Content-Type", "application/x-www-form-urlencoded");

    headers
}

fn run() -> Result<()> {
    let client = reqwest::Client::new();
    let mut res = client.post("https://apis-sandbox.fedex.com/oauth/token")
        .body(input) // 'input' refers to JSON Payload
        .headers(construct_headers())
        .send()?;
    let mut body = String::new();
    res.read_to_string(&mut body)?;

    println!("Status: {}", res.status());
    println!("Headers:\n{:#?}", res.headers());
    println!("Body:\n{}", body);

    Ok(())
}

import Foundation

let headers = [
  "Content-Type": "application/x-www-form-urlencoded"
]
let parameters = [
  input // 'input' refers to JSON Payload
] as [String : Any]

let postData = JSONSerialization.data(withJSONObject: parameters, options: [])

let request = NSMutableURLRequest(url: NSURL(string: "https://apis-sandbox.fedex.com/oauth/token")! as URL,
                                        cachePolicy: .useProtocolCachePolicy,
                                    timeoutInterval: 10.0)
request.httpMethod = "POST"
request.allHTTPHeaderFields = headers
request.httpBody = postData as Data

let session = URLSession.shared
let dataTask = session.dataTask(with: request as URLRequest, completionHandler: { (data, response, error) -> Void in
  if (error != nil) {
    print(error)
  } else {
    let httpResponse = response as? HTTPURLResponse
    print(httpResponse)
  }
})

dataTask.resume()
